  .grid--1x3 {
    font-size: 1.8rem;
    grid-template-columns: repeat(3, 1fr);
  }

  .btn--primary {
    width: 235px;
  }

  .hero-container {
    background-color: $color-bg-primary;
    background-image: url(../images/homepage-bg.png);
    background-repeat: no-repeat;
    background-position-y: -105px;
    background-size: 100%;
    width: 100vw;
    height: 600px;
  }

  #hero-banner-wave {
    background: transparent;
    top: 330px;
  }

  .card-container {
    width: 285px;
    height: 395px;
  }

  .card-image1-container {
    @include image-setter-bigscreen();
  }

  .card-image2-container {
    @include image-setter-bigscreen();
  }

  .card-image3-container {
    @include image-setter-bigscreen();
  }

  .card-text-container {
    text-align: center;
    height: 100px;
    background-color: white;
  }
}

@media screen and (min-width: 768px) {
  .grid--1x2 {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media screen and (min-width: 600px) {
  .btn {
    font-size: 2rem;
  }
}
